// 1Ô∏è‚É£ Recebe os c√≥digos v√°lidos da etapa anterior (filtro do Omie)
const codigosValidos = $items("FiltroVencer", 0).map(item => item.json.codigo_cliente_fornecedor);

// 2Ô∏è‚É£ Filtra os dados da planilha atual (entrada desse Function)
return items.filter(item =>
  codigosValidos.includes(item.json.codigo_cliente_omie)
);

return items.map(item => {
  return {
    json: {
      codigo_cliente_fornecedor: item.json.codigo_cliente_fornecedor,
      codigo_lancamento_omie: item.json.codigo_lancamento_omie,
      valor: item.json.valor,
      vencimento: item.json.vencimento,
      parcela: item.json.parcela,
      // ... passe tudo que precisa junto
    }
  };
});

return items.map((item, index) => {
  const boletoData = item.json;
  const codigoData = $items("Code3")[index].json; // ajuste o nome do n√≥ aqui

  return {
    json: {
      codigo_cliente_fornecedor: codigoData.codigo_cliente_fornecedor,
      codigo_lancamento_omie: codigoData.codigo_lancamento_omie,
      valor: codigoData.valor,
      vencimento: codigoData.vencimento,
      parcela: codigoData.parcela,
      // Dados do boleto
      cLinkBoleto: boletoData.cLinkBoleto,
      codBarras: boletoData.cCodBarras,
      cCodStatus: boletoData.cCodStatus,
      cDesStatus: boletoData.cDesStatus,
      // outros campos do boleto, se quiser
    }
  };
});

// Sempre for√ßa existir apenas item.binary.data
const desiredBinaryKey = 'data';

return items.map((item) => {
  const j = item.json || {};
  const b = item.binary || {};

  // ---- campos b√°sicos do seu input ----
  const codigoCliente = j.codigo_cliente_fornecedor ?? null;
  const codigoLanc    = j.codigo_lancamento_omie != null ? String(j.codigo_lancamento_omie) : '';
  const vencimento    = j.vencimento || 'data n√£o informada';
  const parcela       = j.parcela || '';
  const codBarras     = j.cCodBarras || j.codBarras || 'c√≥digo n√£o dispon√≠vel';

  // valor -> formata√ß√£o pt-BR
  let valorNum = j.valor;
  if (typeof valorNum === 'string') {
    valorNum = parseFloat(valorNum.replace(/\./g, '').replace(',', '.'));
  }
  const valorFmt = isNaN(valorNum) ? '0,00' : valorNum.toFixed(2).replace('.', ',');

  // telefone (se existir nos dados)
  let telefone = (j.telefone || j.telefone1 || j.telefone2 || '')
    .toString()
    .replace(/\D/g, '');
  if (telefone && !telefone.startsWith('55')) telefone = `55${telefone}`;
  const nacional = telefone.replace(/^55/, '');
  const telefoneValido =
    telefone && (nacional.length === 10 || nacional.length === 11) ? telefone : '';

  // caption
  let caption =
    `Ol√° *${j.nome || 'Cliente'}*,\n\n` +
    `Segue seu boleto para pagamento:\n\n` +
    `üí∞ *Valor:* R$ ${valorFmt}\n` +
    `üìÖ *Vencimento:* ${vencimento}\n` +
    `üìÑ *C√≥digo de Barras:* ${codBarras}\n`;

  if (j.pdf_text) caption += `\n(Documento processado automaticamente)`;
  caption += `\n\nObrigado! Qualquer d√∫vida, estamos √† disposi√ß√£o.`;

  // filename
  let filename = 'boleto.pdf';
  const linkBoleto = j.cLinkBoleto || '';
  const m = (linkBoleto || '').match(/([^\/\?]+\.pdf)(?:\?|$)/i);
  if (m) filename = m[1];
  if (b?.file?.fileName && (!m || !m[1])) filename = b.file.fileName;

  // ---- BIN√ÅRIO: for√ßa apenas binary.data ----
  const newBinary = {};
  if (b?.file) {
    newBinary[desiredBinaryKey] = b.file;           // vem do Extract/Download com chave 'file'
  } else if (b && Object.keys(b).length > 0) {
    const firstKey = Object.keys(b)[0];             // qualquer outra chave
    newBinary[desiredBinaryKey] = b[firstKey];
  }

  // ---- JSON final (mantendo campos do input) ----
  const out = {
    json: {
      codigo_cliente_fornecedor: codigoCliente,
      codigo_lancamento_omie:    codigoLanc,
      valor:                     j.valor,
      vencimento:                j.vencimento,
      parcela:                   parcela,

      // extras √∫teis para o envio
      telefone: telefoneValido,
      caption,
      filename,
      url_pdf: linkBoleto || null,
      has_pdf: !!linkBoleto || !!newBinary[desiredBinaryKey],

      // mant√©m texto extra√≠do se houver
      pdf_text: j.pdf_text || null,

      // se seu item vier com "data" em JSON, preserva sem quebrar
      data: j.data ?? null,
    },
  };

  if (newBinary[desiredBinaryKey]) {
    out.binary = newBinary;
  }

  return out;
});

function isFeriado(date) {
  const fixos = ["01/01","21/04","01/05","07/09","12/10","02/11","15/11","25/12"];
  const moveis = new Set([
    // Ex.: "2025-02-03", "2025-02-04", "2025-04-18"
  ]);
  const diaMes = date.toLocaleDateString('pt-BR').substring(0, 5);
  const iso = date.toISOString().slice(0, 10);
  return fixos.includes(diaMes) || moveis.has(iso);
}

const hoje = new Date();
const hojeBR = hoje.toLocaleDateString('pt-BR');

const data = items.flatMap(item => item.json.conta_receber_cadastro || []);

const filtrado = data.filter(item => {
  if (!item?.data_vencimento || !item?.codigo_lancamento_omie) return false;

  // ‚úÖ s√≥ passa t√≠tulos com status "A VENCER"
  if (item?.status_titulo?.toUpperCase() !== "A VENCER") return false;

  const [dia, mes, ano] = item.data_vencimento.split('/');
  const vencimento = new Date(`${ano}-${mes}-${dia}T00:00:00`);

  // 7 dias antes
  const envio = new Date(vencimento);
  envio.setDate(envio.getDate() - 7);

  // Recuar at√© o √∫ltimo dia √∫til se cair em fds/feriado
  while ([0, 6].includes(envio.getDay()) || isFeriado(envio)) {
    envio.setDate(envio.getDate() - 1);
  }

  const envioBR = envio.toLocaleDateString('pt-BR');
  return hojeBR === envioBR && (item.boleto?.cGerado?.toUpperCase() ?? 'S') === 'S';
});

// üîÅ Mapeia campos esperados pelos pr√≥ximos n√≥s
return filtrado.map(item => {
  const raw = item.valor_documento ?? item.valor ?? null;
  let valor = null;
  if (raw != null) {
    valor = (typeof raw === 'number') ? raw :
      parseFloat(String(raw).replace(/\./g, '').replace(',', '.'));
    if (Number.isNaN(valor)) valor = null;
  }

  return {
    json: {
      ...item,
      vencimento: item.data_vencimento,
      valor
    }
  };
});

return items.map((item, index) => {
  // Output do Code3 (dados base)
  const c3 = ($items("Code3")[index] || { json: {} }).json;
  // Output do Code4 (onde sai o c√≥digo de barras)
  const c4 = ($items("Code4")[index] || { json: {} }).json;

  const out = {
    json: {
      // ---- do Code3 ----
      codigo_cliente_fornecedor: c3.codigo_cliente_fornecedor,
      codigo_lancamento_omie: String(c3.codigo_lancamento_omie ?? ''),
      valor: c3.valor,
      vencimento: c3.vencimento,
      parcela: c3.parcela,

      // ---- do item atual ----
      data: item.json.data,

      // ---- do Code4 (c√≥digo de barras) ----
      codBarras: c4.cCodBarras ?? c4.codBarras ?? item.json.cCodBarras ?? null,
      // se quiser manter tamb√©m com o nome original:
      cCodBarras: c4.cCodBarras ?? item.json.cCodBarras ?? null,
    }
  };

  // preserva o bin√°rio como binary.data, se existir
  const b = item.binary || {};
  const chosen =
    b.data || b.file || (Object.keys(b).length ? b[Object.keys(b)[0]] : null);
  if (chosen) out.binary = { data: chosen };

  return out;
});
