const content = $json.output || $json.text || "Sem conteúdo";

// Converte para base64
const base64Data = Buffer.from(content, "utf8").toString("base64");

return [
  {
    json: {
      fileName: "resposta_agente.txt"
    },
    binary: {
      data: {
        data: base64Data,
        mimeType: "text/plain",
        fileName: "resposta_agente.txt"
      }
    }
  }
];
