const data = items.flatMap(item => item.json.conta_receber_cadastro || []);

const filtrado = data.filter(item => {
  if (!item?.data_vencimento || !item?.codigo_lancamento_omie) return false;
  if ((item.status_titulo || '').toUpperCase() !== 'ATRASADO') return false;

  // Parse dd/mm/yyyy -> Date
  const [dia, mes, ano] = item.data_vencimento.split('/');
  const vencimento = new Date(`${ano}-${mes}-${dia}T00:00:00`);

  // Calcula o dia de envio (baseado em dias √∫teis)
  let envio;
  if (isFdsOuFeriado(vencimento)) {
    envio = addDiasUteis(vencimento, 2);
  } else {
    envio = new Date(vencimento);
    envio.setDate(envio.getDate() + 1);
    while (isFdsOuFeriado(envio)) {
      envio.setDate(envio.getDate() + 1);
    }
  }

  const envioBR = envio.toLocaleDateString('pt-BR');

  // ‚úÖ Verifica boleto: queremos apenas os que N√ÉO t√™m boleto gerado
  const cGerado = (item.boleto?.cGerado || '').toUpperCase();
  const semBoleto = cGerado === 'N' || cGerado === '' || !cGerado;

  // ‚úÖ Incluir tamb√©m t√≠tulos vencidos em 15/10/2025
  const vencimentoAlvo = "15/10/2025";

  // Enviar se: √© o dia certo OU √© o vencimento alvo, E ainda n√£o tem boleto
  return (hojeBR === envioBR || item.data_vencimento === vencimentoAlvo) && semBoleto;
});

