:root {
	/* Font Family */
	--font-family: 'Inter', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;

	/* Font Sizes */
	--font-size-body: 14px;
	--font-size-label: 14px;
	--font-size-test-notice: 12px;
	--font-size-input: 14px;
	--font-size-header: 22px;
	--font-size-paragraph: 14px;
	--font-size-link: 13px;
	--font-size-error: 12px;
	--font-size-html-h1: 28px;
	--font-size-html-h2: 22px;
	--font-size-html-h3: 18px;
	--font-size-html-h4: 16px;
	--font-size-html-h5: 14px;
	--font-size-html-h6: 12px;
	--font-size-subheader: 15px;

	/* Colors - Dark Mode */
	--color-background: #171717;
	--color-test-notice-text: #ffb74d;
	--color-test-notice-bg: #2d2816;
	--color-test-notice-border: #54461c;
	--color-card-bg: #222222;
	--color-card-border: #333333;
	--color-card-shadow: rgba(0, 0, 0, 0.25);
	--color-link: #a0a0a0;
	--color-header: #ffffff;
	--color-label: #e0e0e0;
	--color-input-border: #444444;
	--color-input-text: #e0e0e0;
	--color-focus-border: #6366f1;
	--color-submit-btn-bg: #4f46e5;
	--color-submit-btn-text: #ffffff;
	--color-error: #ef4444;
	--color-required: #f59e0b;
	--color-clear-button-bg: #3d3d3d;
	--color-html-text: #d1d5db;
	--color-html-link: #6366f1;
	--color-header-subtext: #9ca3af;

	/* Border Radii */
	--border-radius-card: 10px;
	--border-radius-input: 8px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 10px;

	/* Spacing */
	--padding-container-top: 28px;
	--padding-card: 28px;
	--padding-test-notice-vertical: 14px;
	--padding-test-notice-horizontal: 28px;
	--margin-bottom-card: 20px;
	--padding-form-input: 14px;
	--card-padding: 28px;
	--card-margin-bottom: 20px;

	/* Dimensions */
	--container-width: 460px;
	--submit-btn-height: 50px;
	--checkbox-size: 18px;

	/* Others */
	--box-shadow-card: 0px 8px 24px 0px rgba(0, 0, 0, 0.3);
	--opacity-placeholder: 0.6;
}

/* Adicionar importação da fonte Inter do Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600&display=swap');

/* Estilos adicionais para melhorar a experiência no dark mode */
body {
	background-color: var(--color-background);
	color: var(--color-html-text);
	font-family: var(--font-family);
}

input, textarea, select {
	background-color: #2a2a2a;
	border: 1px solid var(--color-input-border);
	color: var(--color-input-text);
	transition: all 0.2s ease-in-out;
}

input:focus, textarea:focus, select:focus {
	border-color: var(--color-focus-border);
	box-shadow: 0 0 0 2px rgba(99, 102, 241, 0.25);
	outline: none;
}

button[type="submit"] {
	background-color: var(--color-submit-btn-bg);
	border: none;
	font-weight: 500;
	text-transform: none;
	letter-spacing: 0.02em;
	transition: all 0.2s ease;
}

button[type="submit"]:hover {
	background-color: #4338ca;
	box-shadow: 0 4px 12px rgba(99, 102, 241, 0.25);
}

/* Melhorias para checkbox e radio */
input[type="checkbox"], input[type="radio"] {
	accent-color: var(--color-submit-btn-bg);
}

/* Estilo para labels */
label {
	font-weight: 500;
	color: var(--color-label);
	margin-bottom: 8px;
}

/* Estilo para placeholder */
::placeholder {
	color: var(--color-input-text);
	opacity: var(--opacity-placeholder);
}

/* Melhorias para campos obrigatórios */
.required::after {
	content: '*';
	color: var(--color-required);
	margin-left: 4px;
}

