const records = $json.data.messages.records;

// normaliza texto
function getTexto(r) {
  return (r.message?.conversation || r.message?.extendedTextMessage?.text || "")
    .trim()
    .toLowerCase();
}

// filtra s√≥ mensagens enviadas por voc√™ (fromMe = true) e que contenham "resumo da reuni√£o"
let resumos = records.filter(r => {
  const texto = getTexto(r);
  return r.fromMe && texto.startsWith("resumo da reuni√£o");
});

// fallback: se n√£o encontrar nada com fromMe, tenta s√≥ pelo texto
if (resumos.length === 0) {
  resumos = records.filter(r => getTexto(r).startsWith("resumo da reuni√£o"));
}

// pega o mais recente (primeiro do array)
const resumoMaisRecente = resumos.length > 0 ? getTexto(resumos[0]) : null;

// √∫ltima mensagem recebida (feedback do cliente)
const feedback = getTexto(records[0]);

return [{
  json: {
    resumoOriginal: resumoMaisRecente,
    feedbackCliente: feedback
  }
}];

